from flask.ext.wtf import Formfrom wtforms import (StringField, SubmitField, BooleanField, TextAreaField,                     IntegerField)from wtforms.validators import Requiredclass TodoForm(Form):    title = StringField('What is it about?', validators=[Required()])    body = TextAreaField('Description')    priority = IntegerField("Priority")    done = BooleanField('Done')    submit = SubmitField('Add')